import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { DataService } from '../../../core/data.service';

@Component({
  selector: 'app-product-detail',
  templateUrl: './product-detail.component.html',
  styleUrls: ['./product-detail.component.css']
})
export class ProductDetailComponent implements OnInit {
  product: any = null;
  loading = true;
  errorMessage = '';

  constructor(private route: ActivatedRoute, private dataService: DataService) { }

  ngOnInit(): void {
    const productId = Number(this.route.snapshot.paramMap.get('id'));

    if (isNaN(productId) || productId <= 0) {
      this.errorMessage = 'Invalid product ID!';
      this.loading = false;
      return;
    }

    this.dataService.getProducts().subscribe({
      next: (products) => {
        this.product = products.find((product: { id: number; }) => product.id === productId);
        if (!this.product) {
          this.errorMessage = 'Product not found!';
        }
        this.loading = false;
      },
      error: () => {
        this.errorMessage = 'Error fetching product details.';
        this.loading = false;
      }
    });
  }
}
<h2>Product Details</h2>

<p class="product-name">Name: {{ product?.name }}</p>
<p class="product-description">Description: {{ product?.description }}</p>
<p class="product-manufacturer">Manufacturer: {{ product?.manufacturer }}</p>
<p class="product-price">Price: {{ product?.price }}</p>
<p class="product-quantity">Quantity: {{ product?.quantity }}</p>
